jQuery(document).ready(function($) {
    if (typeof NXP.top_nav != 'undefined') {
        $('#header .megamenu .navbar-center').html(NXP.top_nav);
    } // load top nav elements
    if (typeof NXP.footer_nav != 'undefined') {
        $('footer').html(NXP.footer_nav);
    } // load footer elements
    if (typeof NXP.NEWS != 'undefined' && typeof NXP.NEWS.channel != 'undefined') {
        NXP.NEWS.load(NXP.NEWS)
    }; //load and start news.  Requires FB_FSL_PRESSRELEASES to be loaded first
    $(document).on('click', '.megamenu .dropdown-menu', function(e) {
        e.stopPropagation()
    });
    document.getElementById("dnavs").value = "";
    $("#All").hide();
    $('#ProductSelector').mouseover(function(e) {
        $("#parts").prop('disabled', true);
        // $("#parts").css('background-color', '#DEDEDE');
        // $("#parts").css('color', '#DEDEDE');
    });
    $('#ProductSelector').mouseout(function(e) {
        if (!$(this).data("sticky")) {
            $("#parts").prop('disabled', false);
            // $("#parts").css('background-color', '#FFFFFF');
            // $("#parts").css('color', '#666666');
        }
    });
    $('#search-choices li').click(function() {
        document.getElementById("client").value = $(this).attr('name');
        document.getElementById("Default-choice-list").innerHTML = $(this).text()+" ";
        if ("ProductSelector" == document.getElementById("client").value) {
            $(this).data("sticky", true);
            $("#parts").prop('disabled', true);
            $("#parts").css('background-color', '#DEDEDE');
            $("#parts").css('color', '#DEDEDE');
            window.location = "http://uat.freescale.com/webapp/parametricSelector.sp?";
        }
        if ($(this).text() != "All") {
            document.getElementById("dnavs").value = $(this).attr('id');
            $('ul li').show();
            $(this).hide();
        } else {
            $('ul li').show();
            $("#All").hide();
        }
    });
});
